import React, { useEffect, useState } from 'react';
import axiosClient from '../../services/axios';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import * as actions from '../../store/actions/index';

export default function Index(props) {
    const [family, setFamily] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [members, setMembers] = useState(familyTree.family_members);
    const [newMember, setNewMember] = useState({ name: '', relationship: '', parent_id: null });

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(actions.loadingStart());
        let mounted = true;
        axiosClient.get('entry/initial_payload')
        .then(res => {
            if (mounted) {
                setIsAuthenticated(res.data.logged_in);
                getFamily();
            }
        })
        .catch(err => {
            dispatch(actions.setErrors(err.response.data.errors));
        });
        dispatch(actions.loadingFinish());
        return () => {
            mounted = false;
        }
    }, []);

    const postFamilyTree = (e) => {
        e.preventDefault();
        let body = {
            family_tree: {
                "name": familyTreeName
            }
        }
        axiosClient.post(`family_trees/create`, body)
        .then(res => {
            setFamily(res.data)
        })
        .catch(err => {
            dispatch(actions.setErrors(err.response.data.errors));
        });
    }

    const getFamily = () => {
        axiosClient.get(`family_trees/${props.match.params.id}`)
        .then(res => {
            if (res.data) {
                console.log(res.data)
                setFamily(res.data)
            }
        })
        .catch(err => {
            dispatch(actions.setErrors(err.response.data.errors));
        });
    }

    return (
        <div>
          <input 
            value={newMember.name} 
            onChange={e => setNewMember({ ...newMember, name: e.target.value })} 
            placeholder="Member Name" 
          />
          <select 
            value={newMember.relationship} 
            onChange={e => setNewMember({ ...newMember, relationship: e.target.value })}
          >
            <option value="">Select Relationship</option>
            <option value="father">Father</option>
            <option value="mother">Mother</option>
            <option value="brother">Brother</option>
            <option value="sister">Sister</option>
            <option value="son">Son</option>
            <option value="daughter">Daughter</option>
          </select>
          <button onClick={handleAddMember}>Add Member</button>
          <ul>
            {members.map(member => (
              <li key={member.id}>{member.name} ({member.relationship})</li>
            ))}
          </ul>
        </div>
    );
}
